draw((5,0)--(5,3), linewidth(2)); 
draw((5,3)--(7,3), linewidth(2)); 
draw((7,3)--(7,4), linewidth(2)); 
draw((7,4)--(4,4), linewidth(2)); 
draw((4,4)--(4,1), linewidth(2)); 
draw((4,1)--(0,1), linewidth(2)); 
draw((0,1)--(0,0), linewidth(2)); 
draw((6,3)--(6,4), linewidth(2)); 
