draw((4,0)--(4,1), linewidth(2)); 
draw((3,0)--(3,1), linewidth(2)); 
draw((2,0)--(2,1), linewidth(2)); 
draw((1,1)--(1,0), linewidth(2)); 
 /* dots and labels */ 
clip((xmin,ymin)--(xmin,ymax)--(xmax,ymax)--(xmax,ymin)--cycle); 
 /* end of picture */
